const EmptyState = ({ title, description, icon, action }) => (
  <div className="flex flex-col items-center gap-4 rounded-3xl border border-dashed border-white/15 bg-white/5 px-8 py-16 text-center">
    {icon && <div className="text-emerald-300">{icon}</div>}
    <h3 className="text-xl font-semibold text-white">{title}</h3>
    {description && <p className="max-w-md text-sm text-white/70">{description}</p>}
    {action}
  </div>
);

export default EmptyState;
