import { Github, Linkedin, Twitter } from 'lucide-react';

const socials = [
  { icon: Github, label: 'GitHub', href: 'https://github.com' },
  { icon: Twitter, label: 'Twitter', href: 'https://twitter.com' },
  { icon: Linkedin, label: 'LinkedIn', href: 'https://linkedin.com' },
];

const Footer = () => {
  return (
    <footer className="border-t border-white/10 bg-slate-950/70 text-sm text-white/60">
      <div className="mx-auto flex max-w-6xl flex-col items-center justify-between gap-4 px-4 py-6 text-center md:flex-row md:text-left">
        <div>
          <p className="text-white/80">© {new Date().getFullYear()} BookLoop</p>
          <p className="text-xs text-white/50">
            Built with ❤️ for readers. File-based backend, ready to deploy anywhere.
          </p>
        </div>
        <div className="flex items-center gap-4">
          {socials.map(({ icon: Icon, ...item }) => (
            <a
              key={item.label}
              className="flex items-center gap-2 rounded-full border border-white/10 px-3 py-1 transition hover:border-white/30 hover:text-white"
              href={item.href}
              target="_blank"
              rel="noreferrer"
            >
              <Icon className="h-4 w-4" />
              <span className="text-xs font-semibold uppercase tracking-wide">{item.label}</span>
            </a>
          ))}
        </div>
      </div>
    </footer>
  );
};

export default Footer;
