import React, { useState, useEffect } from 'react';
import { 
  Book, 
  Search, 
  PlusCircle, 
  Library, 
  User, 
  LogOut, 
  RefreshCw, 
  MapPin,
  Heart,
  ArrowRight
} from 'lucide-react';

// Use the server API (file-based) instead of Firebase.
const API_BASE = '';
const BOOKS_API = `${API_BASE}/api/books`;

// --- Components ---

const Navbar = ({ user, setView, currentView, handleLogin, handleLogout }) => (
  <nav className="bg-white border-b sticky top-0 z-50 px-4 py-3 shadow-sm">
    <div className="max-w-6xl mx-auto flex justify-between items-center">
      <div 
        className="flex items-center gap-2 cursor-pointer hover:opacity-80 transition" 
        onClick={() => setView('home')}
      >
        <div className="bg-emerald-600 p-2 rounded-lg">
          <Book className="w-6 h-6 text-white" />
        </div>
        <span className="text-xl font-bold text-gray-800 tracking-tight">BookLoop</span>
      </div>

      <div className="flex items-center gap-6">
        <button 
          onClick={() => setView('browse')}
          className={`text-sm font-medium transition ${currentView === 'browse' ? 'text-emerald-600' : 'text-gray-500 hover:text-gray-800'}`}
        >
          Browse Books
        </button>
        <button 
          onClick={() => setView('chat')}
          className={`text-sm font-medium transition ${currentView === 'chat' ? 'text-emerald-600' : 'text-gray-500 hover:text-gray-800'}`}
        >
          Chat
        </button>
        
        {user ? (
          <>
            <button 
              onClick={() => setView('add')}
              className={`text-sm font-medium transition flex items-center gap-1 ${currentView === 'add' ? 'text-emerald-600' : 'text-gray-500 hover:text-gray-800'}`}
            >
              <PlusCircle className="w-4 h-4" />
              Add Book
            </button>
            <div className="flex items-center gap-3 pl-4 border-l">
              <div className="w-8 h-8 bg-emerald-100 rounded-full flex items-center justify-center text-emerald-700 font-bold text-xs">
                {(user?.id || '').slice(0,2).toUpperCase()}
              </div>
              <button 
                onClick={handleLogout}
                className="text-gray-400 hover:text-red-500 transition"
                title="Logout"
              >
                <LogOut className="w-5 h-5" />
              </button>
            </div>
          </>
        ) : (
          <div className="flex items-center gap-3">
            <button onClick={() => setView('signin')} className="text-sm text-gray-700 hover:text-gray-900">Sign In</button>
            <button onClick={() => setView('signup')} className="bg-emerald-600 text-white px-3 py-1 rounded-md text-sm">Sign Up</button>
          </div>
        )}
      </div>
    </div>
  </nav>
);

const Hero = ({ setView, handleLogin, user }) => (
  <div className="bg-gradient-to-b from-emerald-50 to-white pt-20 pb-24 px-4">
    <div className="max-w-4xl mx-auto text-center">
      <div className="inline-flex items-center gap-2 bg-emerald-100 text-emerald-800 px-3 py-1 rounded-full text-xs font-bold uppercase tracking-wider mb-6">
        <RefreshCw className="w-3 h-3" />
        The #1 Book Exchange Platform
      </div>
      <h1 className="text-5xl md:text-6xl font-extrabold text-gray-900 mb-6 leading-tight">
        Give a Book. <br/>
        <span className="text-transparent bg-clip-text bg-gradient-to-r from-emerald-600 to-teal-500">
          Get a Book.
        </span>
      </h1>
      <p className="text-xl text-gray-600 mb-10 max-w-2xl mx-auto leading-relaxed">
        Join a community of readers. Upload the books gathering dust on your shelf and swap them for your next great adventure.
      </p>
      <div className="flex flex-col sm:flex-row items-center justify-center gap-4">
        <button 
          onClick={() => setView('browse')}
          className="w-full sm:w-auto px-8 py-4 bg-emerald-600 hover:bg-emerald-700 text-white rounded-xl font-bold text-lg transition shadow-xl hover:shadow-2xl hover:-translate-y-1"
        >
          Start Browsing
        </button>
        {!user && (
          <button 
            onClick={handleLogin}
            className="w-full sm:w-auto px-8 py-4 bg-white border border-gray-200 text-gray-700 hover:bg-gray-50 rounded-xl font-bold text-lg transition"
          >
            Join Now
          </button>
        )}
      </div>
      
      {/* Stats */}
      <div className="mt-16 grid grid-cols-3 gap-8 max-w-2xl mx-auto border-t pt-8">
        <div>
          <div className="text-3xl font-bold text-gray-900">2k+</div>
          <div className="text-sm text-gray-500 mt-1">Active Readers</div>
        </div>
        <div>
          <div className="text-3xl font-bold text-gray-900">5k+</div>
          <div className="text-sm text-gray-500 mt-1">Books Swapped</div>
        </div>
        <div>
          <div className="text-3xl font-bold text-gray-900">100%</div>
          <div className="text-sm text-gray-500 mt-1">Free to Join</div>
        </div>
      </div>
    </div>
  </div>
);

const BookCard = ({ book, user, handleExchange }) => {
  return (
    <div className="group bg-white rounded-2xl border border-gray-100 shadow-sm hover:shadow-xl transition-all duration-300 flex flex-col overflow-hidden h-full hover:-translate-y-1">
      <div className="h-48 overflow-hidden relative bg-gray-100">
        {book.imageUrl ? (
          <img 
            src={book.imageUrl} 
            alt={book.title} 
            className="w-full h-full object-cover transition duration-500 group-hover:scale-105"
            onError={(e) => {
              e.target.src = 'https://images.unsplash.com/photo-1543002588-bfa74002ed7e?auto=format&fit=crop&q=80&w=2730';
            }}
          />
        ) : (
          <div className="w-full h-full flex items-center justify-center text-gray-300 bg-slate-50">
            <Book className="w-16 h-16" />
          </div>
        )}
        <div className="absolute top-3 right-3 bg-white/90 backdrop-blur-sm px-2 py-1 rounded-md text-xs font-bold text-gray-700 shadow-sm">
          {book.condition}
        </div>
      </div>
      
      <div className="p-5 flex flex-col flex-grow">
        <div className="text-xs font-semibold text-emerald-600 mb-2 uppercase tracking-wider">{book.genre || 'General'}</div>
        <h3 className="text-lg font-bold text-gray-900 mb-1 leading-tight">{book.title}</h3>
        <p className="text-sm text-gray-500 mb-4">by {book.author}</p>
        
        <div className="flex items-center gap-2 text-xs text-gray-400 mb-6 mt-auto">
          <MapPin className="w-3 h-3" />
          <span>{book.location || 'Remote'}</span>
        </div>

        <div className="flex items-center justify-between pt-4 border-t border-gray-50 mt-auto">
           <div className="flex items-center gap-2">
             <div className="w-6 h-6 rounded-full bg-gradient-to-br from-purple-400 to-indigo-500 text-[10px] text-white flex items-center justify-center">
               {book.ownerId ? book.ownerId.slice(0,2).toUpperCase() : '??'}
             </div>
             <span className="text-xs text-gray-500">Owner</span>
           </div>

           {user && user.uid === book.ownerId ? (
             <span className="text-xs font-medium text-gray-400 bg-gray-100 px-3 py-1 rounded-full">Your Book</span>
           ) : (
             <button 
                onClick={() => handleExchange(book)}
                disabled={book.status === 'exchanged'}
                className={`text-sm font-medium px-4 py-2 rounded-lg transition-colors ${
                  book.status === 'exchanged' 
                  ? 'bg-gray-100 text-gray-400 cursor-not-allowed' 
                  : 'bg-emerald-600 hover:bg-emerald-700 text-white'
                }`}
             >
               {book.status === 'exchanged' ? 'Taken' : 'Request'}
             </button>
           )}
        </div>
      </div>
    </div>
  );
};

const Browse = ({ books, loading, user, handleExchange }) => {
  const [searchTerm, setSearchTerm] = useState('');
  
  const filteredBooks = books.filter(book => 
    book.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
    book.author.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <div className="max-w-6xl mx-auto px-4 py-8">
      <div className="flex flex-col md:flex-row md:items-center justify-between mb-8 gap-4">
        <div>
          <h2 className="text-2xl font-bold text-gray-900">Explore Collection</h2>
          <p className="text-gray-500">Find your next favorite read.</p>
        </div>
        
        <div className="relative w-full md:w-96">
          <Search className="absolute left-3 top-1/2 -translate-y-1/2 w-5 h-5 text-gray-400" />
          <input 
            type="text" 
            placeholder="Search by title or author..." 
            className="w-full pl-10 pr-4 py-3 rounded-xl border border-gray-200 focus:border-emerald-500 focus:ring-2 focus:ring-emerald-200 outline-none transition"
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
          />
        </div>
      </div>

      {loading ? (
        <div className="flex justify-center items-center h-64">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-emerald-600"></div>
        </div>
      ) : filteredBooks.length === 0 ? (
        <div className="text-center py-20 bg-gray-50 rounded-2xl border border-dashed border-gray-200">
          <Book className="w-12 h-12 text-gray-300 mx-auto mb-3" />
          <h3 className="text-lg font-medium text-gray-900">No books found</h3>
          <p className="text-gray-500">Try adjusting your search terms.</p>
        </div>
      ) : (
        <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-6">
          {filteredBooks.map(book => (
            <BookCard key={book.id} book={book} user={user} handleExchange={handleExchange} />
          ))}
        </div>
      )}
    </div>
  );
};

const AddBook = ({ setView, user, token }) => {
  const [loading, setLoading] = useState(false);
  const [formData, setFormData] = useState({
    title: '',
    author: '',
    genre: 'Fiction',
    condition: 'Good',
    location: '',
    imageUrl: ''
  });

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!user) return;
    setLoading(true);

    try {
      // Post to server
      await fetch(BOOKS_API, { method: 'POST', headers: { 'Content-Type': 'application/json', Authorization: token ? `Bearer ${token}` : '' }, body: JSON.stringify({ ...formData, ownerId: user.id }) });
      setView('browse');
    } catch (error) {
      console.error("Error adding book:", error);
      alert("Failed to add book. Try again.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="max-w-2xl mx-auto px-4 py-12">
      <div className="bg-white p-8 rounded-2xl shadow-lg border border-gray-100">
        <div className="flex items-center gap-3 mb-8">
          <div className="bg-emerald-100 p-3 rounded-full">
            <PlusCircle className="w-6 h-6 text-emerald-600" />
          </div>
          <div>
            <h2 className="text-2xl font-bold text-gray-900">List a Book</h2>
            <p className="text-gray-500 text-sm">Share a book with the community</p>
          </div>
        </div>

        <form onSubmit={handleSubmit} className="space-y-6">
          <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
            <div className="space-y-2">
              <label className="text-sm font-semibold text-gray-700">Book Title</label>
              <input 
                required
                className="w-full px-4 py-3 rounded-lg border border-gray-200 focus:ring-2 focus:ring-emerald-100 focus:border-emerald-500 outline-none transition"
                placeholder="e.g. The Great Gatsby"
                value={formData.title}
                onChange={e => setFormData({...formData, title: e.target.value})}
              />
            </div>
            <div className="space-y-2">
              <label className="text-sm font-semibold text-gray-700">Author</label>
              <input 
                required
                className="w-full px-4 py-3 rounded-lg border border-gray-200 focus:ring-2 focus:ring-emerald-100 focus:border-emerald-500 outline-none transition"
                placeholder="e.g. F. Scott Fitzgerald"
                value={formData.author}
                onChange={e => setFormData({...formData, author: e.target.value})}
              />
            </div>
          </div>

          <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
            <div className="space-y-2">
              <label className="text-sm font-semibold text-gray-700">Genre</label>
              <select 
                className="w-full px-4 py-3 rounded-lg border border-gray-200 focus:ring-2 focus:ring-emerald-100 focus:border-emerald-500 outline-none transition bg-white"
                value={formData.genre}
                onChange={e => setFormData({...formData, genre: e.target.value})}
              >
                <option>Fiction</option>
                <option>Non-Fiction</option>
                <option>Sci-Fi</option>
                <option>Mystery</option>
                <option>Biography</option>
                <option>Textbook</option>
              </select>
            </div>
            <div className="space-y-2">
              <label className="text-sm font-semibold text-gray-700">Condition</label>
              <select 
                className="w-full px-4 py-3 rounded-lg border border-gray-200 focus:ring-2 focus:ring-emerald-100 focus:border-emerald-500 outline-none transition bg-white"
                value={formData.condition}
                onChange={e => setFormData({...formData, condition: e.target.value})}
              >
                <option>New</option>
                <option>Like New</option>
                <option>Good</option>
                <option>Fair</option>
                <option>Poor</option>
              </select>
            </div>
          </div>

          <div className="space-y-2">
            <label className="text-sm font-semibold text-gray-700">Cover Image URL</label>
            <input 
              type="url"
              className="w-full px-4 py-3 rounded-lg border border-gray-200 focus:ring-2 focus:ring-emerald-100 focus:border-emerald-500 outline-none transition"
              placeholder="https://example.com/image.jpg"
              value={formData.imageUrl}
              onChange={e => setFormData({...formData, imageUrl: e.target.value})}
            />
            <p className="text-xs text-gray-500">Tip: Search Google Images, right click image, "Copy Image Address"</p>
          </div>

          <div className="space-y-2">
            <label className="text-sm font-semibold text-gray-700">City/Location</label>
            <input 
              required
              className="w-full px-4 py-3 rounded-lg border border-gray-200 focus:ring-2 focus:ring-emerald-100 focus:border-emerald-500 outline-none transition"
              placeholder="e.g. New York, NY"
              value={formData.location}
              onChange={e => setFormData({...formData, location: e.target.value})}
            />
          </div>

          <div className="pt-4 flex gap-4">
            <button 
              type="button"
              onClick={() => setView('browse')}
              className="flex-1 px-6 py-3 border border-gray-200 text-gray-700 font-semibold rounded-xl hover:bg-gray-50 transition"
            >
              Cancel
            </button>
            <button 
              type="submit"
              disabled={loading}
              className="flex-1 px-6 py-3 bg-emerald-600 hover:bg-emerald-700 text-white font-semibold rounded-xl shadow-lg shadow-emerald-200 transition flex justify-center items-center"
            >
              {loading ? <span className="animate-spin rounded-full h-5 w-5 border-b-2 border-white"></span> : 'List Book'}
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

// --- Main App Component ---

export default function App() {
  const [user, setUser] = useState(null);
  const [books, setBooks] = useState([]);
  const [loading, setLoading] = useState(true);
  const [currentView, setCurrentView] = useState('home'); // home, browse, add
  const [notification, setNotification] = useState(null);
  const [token, setToken] = useState(null);

  const apiFetch = (url, opts = {}) => {
    const headers = opts.headers || {};
    if (token) headers['Authorization'] = `Bearer ${token}`;
    return fetch(url, { ...opts, headers });
  };

  // 1. Auth Handling (JWT stored in localStorage)
  useEffect(() => {
    const t = localStorage.getItem('be_token');
    const u = localStorage.getItem('be_user');
    if (t) setToken(t);
    if (u) setUser(JSON.parse(u));
    // verify token and refresh user
    if (t) {
      fetch('/api/me', { headers: { Authorization: `Bearer ${t}` } })
        .then(r => r.json())
        .then(data => { if (!data.error) { setUser(data); localStorage.setItem('be_user', JSON.stringify(data)); } else { localStorage.removeItem('be_token'); localStorage.removeItem('be_user'); setToken(null); setUser(null); } })
        .catch(() => {});
    }
  }, []);

  // 2. Data Fetching
  const fetchBooks = async () => {
    setLoading(true);
    try {
      const res = await fetch(BOOKS_API);
      const data = await res.json();
      setBooks(data || []);
    } catch (err) {
      console.error('Fetch books error', err);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => { fetchBooks(); }, []);

  // Actions
  const handleLogin = () => setCurrentView('signin');

  const doSignup = async (name, email, password) => {
    try {
      const res = await fetch('/api/signup', { method: 'POST', headers: { 'Content-Type': 'application/json' }, body: JSON.stringify({ name, email, password }) });
      const data = await res.json();
      if (data.token) {
        localStorage.setItem('be_token', data.token);
        localStorage.setItem('be_user', JSON.stringify(data.user));
        setToken(data.token);
        setUser(data.user);
        setCurrentView('browse');
        showNotification('Account created', 'success');
      } else {
        showNotification(data.error || 'Signup failed', 'error');
      }
    } catch (err) { showNotification('Signup failed', 'error'); }
  };

  const doLogin = async (email, password) => {
    try {
      const res = await fetch('/api/login', { method: 'POST', headers: { 'Content-Type': 'application/json' }, body: JSON.stringify({ email, password }) });
      const data = await res.json();
      if (data.token) {
        localStorage.setItem('be_token', data.token);
        localStorage.setItem('be_user', JSON.stringify(data.user));
        setToken(data.token);
        setUser(data.user);
        setCurrentView('browse');
        showNotification('Signed in', 'success');
      } else {
        showNotification(data.error || 'Login failed', 'error');
      }
    } catch (err) { showNotification('Login failed', 'error'); }
  };

  const handleLogout = () => {
    localStorage.removeItem('be_user');
    localStorage.removeItem('be_token');
    setUser(null);
    setToken(null);
    setCurrentView('home');
    showNotification('Logged out', 'default');
  };

  const handleExchange = async (book) => {
    if (!user) { showNotification('Please sign in to request books.', 'error'); return; }
    if (!confirm(`Request to exchange "${book.title}"?`)) return;
    try {
      const res = await apiFetch(`${BOOKS_API}/${book.id}`, {
        method: 'PUT', headers: {'Content-Type':'application/json'},
        body: JSON.stringify({ status: 'exchanged', exchangedTo: user.id, exchangedAt: new Date().toISOString() })
      });
      if (!res.ok) throw new Error('Failed');
      showNotification('Request sent! Happy reading.', 'success');
      fetchBooks();
    } catch (err) {
      console.error(err);
      showNotification('Failed to process exchange.', 'error');
    }
  };

  const showNotification = (message, type = 'default') => {
    setNotification({ message, type });
    setTimeout(() => setNotification(null), 3000);
  };

  // Helper for View Rendering
  const renderView = () => {
    switch (currentView) {
      case 'home':
        return <Hero setView={setCurrentView} handleLogin={handleLogin} user={user} />;
      case 'browse':
        return <Browse books={books} loading={loading} user={user} handleExchange={handleExchange} />;
      case 'add':
        return user ? <AddBook setView={setCurrentView} user={user} token={token} /> : <Hero setView={setCurrentView} handleLogin={handleLogin} user={user} />;
      case 'signin':
        return <SignIn onLogin={doLogin} onSwitch={() => setCurrentView('signup')} />;
      case 'signup':
        return <SignUp onSignup={doSignup} onSwitch={() => setCurrentView('signin')} />;
      case 'chat':
        return <Chat user={user} token={token} />;
      case 'profile':
        return <Profile user={user} onUpdate={(u)=>{ setUser(u); localStorage.setItem('be_user', JSON.stringify(u)); }} token={token} />;
      default:
        return <Hero setView={setCurrentView} handleLogin={handleLogin} user={user} />;
    }
  };

  return (
    <div className="min-h-screen bg-white font-sans text-gray-900 pb-10">
      <Navbar 
        user={user} 
        setView={setCurrentView} 
        currentView={currentView}
        handleLogin={handleLogin}
        handleLogout={handleLogout}
      />
      
      {/* Notification Toast */}
      {notification && (
        <div className={`fixed top-20 right-4 z-50 px-6 py-3 rounded-lg shadow-xl border animate-fade-in-down ${
          notification.type === 'success' ? 'bg-emerald-100 border-emerald-200 text-emerald-800' :
          notification.type === 'error' ? 'bg-red-100 border-red-200 text-red-800' :
          'bg-gray-800 text-white'
        }`}>
          {notification.message}
        </div>
      )}

      <main>
        {renderView()}
      </main>
    </div>
  );
}
